import React,{useState,useEffect}from'react';
import SearchBar from'../searchbar/searchbar';
import Product from '../product/product';
import ProductList from'./productlist';

const SearchPage=(props)=>{
  const[input,setInput]=useState('');
  const[productListDefault,setProductListDefault]=useState();
  const[productList,setProductList]=useState();

  const fetchData=async()=>{
    return await fetch('http://localhost:3004/product').then(response=>response.json()).then(data=>{
      setProductList(data);
      setProductListDefault(data);
    });}
  const updateInput=async(input)=>{
    const filtered=productListDefault.filter(product=>{
      return product.title.toLowerCase().includes(input.toLowerCase())})
      setInput(input);
      setProductList(filtered);
    }
    useEffect(()=>{fetchData()},[]);
    return(
    <>
      <h1>Product List</h1>
      <SearchBar input={input}
        onChange={updateInput}/>
      </>
    );
  }
export default SearchPage;
